import React from "react";
import { useDispatch, useSelector } from "react-redux";
import AuthStyle from "../../../styles/Auth/Auth.css";
import GlobalStyle from "../../../styles/Global.css";
import Regist from "./TypeAuth/Regist";
import Login from "./TypeAuth/Login";
import Button from '@mui/joy/Button';
import { changeTypeAuth } from "../../../redux/reducers/TypeAuth";

const responseVk = (response) => {
    console.log(response);
}

const { ContainerAuth, ContainerBtnsNav } = AuthStyle
const { Container } = GlobalStyle

function Auth() {

    const dispatch = useDispatch()

    const typeAuth = useSelector(state => state.TypeAuth.typeAuth)

    function switchTypeAuth(type) {
        if (type === "login") {
            dispatch(changeTypeAuth("login"))
        } else {
            dispatch(changeTypeAuth("reg"))
        }
    }

    return (
        <Container>
            <ContainerAuth>
                <ContainerBtnsNav>
                    <Button type="submit" onClick={() => switchTypeAuth("login")} size="sm">Войти</Button>
                    <Button type="submit" onClick={() => switchTypeAuth("reg")} size="sm">Создать аккаунт</Button>
                </ContainerBtnsNav>
                {typeAuth === "reg" ? <Regist></Regist> : <Login></Login>}
            </ContainerAuth>
        </Container>
    )
}

export default Auth
