import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "../../axios";


export const fetchUserForChoice = createAsyncThunk("Auth/", async () => {
    try {
        const { data } = await axios.get("/Auth")
        let dataCategory = data
        return dataCategory
    } catch (error) {
        console.log(error)
    }
})


const initialState = {
    typeAuth: "login"
}

export const TypeAuthSlice = createSlice({
    name: "TypeAuth",
    initialState,
    reducers: {
        changeTypeAuth: (state, action) => {
            state.typeAuth = action.payload
        }
    },
    extraReducers: builder => {
        // Get Answers
        builder.addCase(fetchUserForChoice.pending, (state) => {
            state.UsersStatus = "loading";
        })
        builder.addCase(fetchUserForChoice.fulfilled, (state, action) => {
            state.Users = action.payload
            state.UsersStatus = "loaded";
        })
        builder.addCase(fetchUserForChoice.rejected, (state) => {
            state.UsersStatus = "error";
        })
    }
})

export const { changeTypeAuth } = TypeAuthSlice.actions

export default TypeAuthSlice.reducer
