import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "../../axios";


const initialState = {
    typeAuth: "login",
    request: ""
}

export const fetchLogin = createAsyncThunk("Auth/fetchLogin", async (params) => {
    try {
        const { data } = await axios.get("/Auth")
        let dataLogin = data
        return dataLogin
    } catch (error) {
        initialState.request = error
    }
})

export const fetchRegister = createAsyncThunk("Auth/fetchRegister", async (params) => {
    try {
        const { data } = await axios.post("/Auth", params.data)
        let dataRegist = data
        return dataRegist
    } catch (error) {
        initialState.request = error.response.data
        console.log(error.response.data)
    }
})

export const TypeAuthSlice = createSlice({
    name: "TypeAuth",
    initialState,
    reducers: {
        changeTypeAuth: (state, action) => {
            state.typeAuth = action.payload
        },
        changeRequestRegist: (state, ac)
    },
    extraReducers: builder => {
        // Get Answers
        builder.addCase(fetchLogin.pending, (state) => {
            state.UsersStatus = "loading";
        })
        builder.addCase(fetchLogin.fulfilled, (state, action) => {
            state.Users = action.payload
            state.UsersStatus = "loaded";
        })
        builder.addCase(fetchLogin.rejected, (state) => {
            state.UsersStatus = "error";
        })
    },
    extraReducers: builder => {
        // Get Answers
        builder.addCase(fetchRegister.pending, (state) => {
            state.UsersStatus = "loading";
        })
        builder.addCase(fetchRegister.fulfilled, (state, action) => {
            console.log(action)
            state.request = "Регистрация прошла успешно";
        })
        builder.addCase(fetchRegister.rejected, (state) => {
            state.UsersStatus = "error";
        })
    }
})

export const { changeTypeAuth } = TypeAuthSlice.actions

export default TypeAuthSlice.reducer
