import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";


export const fetchUserForChoice = createAsyncThunk("Asses/fetchCategories", async () => {
    try {
        const { data } = await axios.get("/Asses")
        let dataCategory = data
        return dataCategory
    } catch (error) {
        console.log(error)
    }
})

const initialState = {
    Users: [],
    UsersStatus: "loading"
}

export const ShowUserForChoiceSlice = createSlice({
    name: "ShowUsers",
    initialState,
    extraReducers: builder => {
        // Get Answers
        builder.addCase(fetchUserForChoice.pending, (state) => {
            state.UsersStatus = "loading";
        })
        builder.addCase(fetchUserForChoice.fulfilled, (state, action) => {
            state.Users = action.payload
            state.UsersStatus = "loaded";
        })
        builder.addCase(fetchUserForChoice.rejected, (state) => {
            state.UsersStatus = "error";
        })
    }
})

export const { clearItemsAnswer, setTittleCategory } = ShowUserForChoiceSlice.actions

export default ShowUserForChoiceSlice.reducer
