import React from "react";
import { useEffect } from "react";
import AssesStyle from "../../styles/Asses/Asses.css";
import SideBarComp from "../global/SideBar";
import LogoComp from "../global/Logo";
import { fetchUserForChoice } from "../../redux/reducers/ShowUserForChoice";
import { useDispatch, useSelector } from "react-redux";
import { fetchLikeUser } from "../../redux/reducers/ShowUserForChoice";

const { ContainerAsses, ContainerBidder, ContainerBtns, Btn, ContainerAboutBidder, NameBidder, AboutBidder, ListHobbies, Hobby } = AssesStyle


function Asses() {

    const dispatch = useDispatch()

    const setterUser = useSelector(state => state.Auth.user)[0]

    let getterUser = useSelector(state => state.ShowUsers.UserForChoice),
        UserForChoiceStatus = useSelector(state => state.ShowUsers.UserForChoiceStatus)

    let data = {
        setterId: setterUser._id,
        setterGender: setterUser.Gender
    }

    useEffect(() => {
        dispatch(fetchUserForChoice({ data, dispatch }))
        console.log(getterUser)
    }, [])

    function rate(rating) {
        console.log(rating)
        let dataRate = {
            setterId: setterUser._id,
            getterId: getterUser._id,
            rating: rating
        }
        dispatch(fetchLikeUser({ dataRate, dispatch }))
        dispatch(fetchUserForChoice({ data, dispatch }))
    }

    let flag = false

    if (UserForChoiceStatus == "loaded" && !getterUser.Name) {
        
    }
    console.log(getterUser)

    return (
        <>
            <LogoComp></LogoComp>
            <SideBarComp></SideBarComp>
            <ContainerAsses>
                <ContainerBidder>
                    <img src="https://catherineasquithgallery.com/uploads/posts/2021-02/1614344178_47-p-chelovek-na-svetlom-fone-51.jpg" alt="" srcset="" style={{ height: "max-content" }} />
                    <ContainerAboutBidder>
                        <NameBidder>{
                            UserForChoiceStatus = "loaded"
                                ? getterUser.Name
                                : "Имя загружается"
                        }</NameBidder>
                        <AboutBidder>{
                            UserForChoiceStatus = "loaded"
                                ? getterUser.About
                                : "Имя загружается"
                        }</AboutBidder>
                        <ListHobbies className="list-reset">
                            {
                                !!getterUser
                                    ? getterUser.listHobby.map((el) => {
                                        return <Hobby>{el}</Hobby>
                                    })
                                    : "Имя загружается"
                            }
                        </ListHobbies>
                    </ContainerAboutBidder>
                </ContainerBidder>
                <ContainerBtns>
                    <Btn onClick={() => { rate("dislike") }}>
                        <img src="https://cdn-icons-png.freepik.com/512/12483/12483938.png?ga=GA1.1.2011989677.1715850799" alt="Не нравится" srcset="" style={{ width: "40px", height: "40px", margin: "0px" }} />
                    </Btn>
                    <Btn onClick={() => { rate("like") }}>
                        <img src="https://img.icons8.com/?size=256&id=oEc1O2tvXKUb&format=png" alt="Нравится" srcset="" style={{ width: "50px", height: "50px", margin: "0px" }} />
                    </Btn>
                </ContainerBtns>
            </ContainerAsses>
        </>
    )
}

export default Asses
