import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import AuthStyle from "../../../styles/Auth/Auth.css";
import GlobalStyle from "../../../styles/Global.css";
import Regist from "./TypeAuth/Regist";
import Login from "./TypeAuth/Login";
import LogoComp from "../../global/Logo";
import Button from '@mui/joy/Button';
import { changeTypeAuth } from "../../../redux/reducers/Auth";
import Recaptcha from 'react-recaptcha'


const { ContainerAuth, ContainerBtnsNav, ContainerCaptha } = AuthStyle
const { Container } = GlobalStyle

function Auth() {

    const dispatch = useDispatch(),
          navigate = useNavigate()

    const typeAuth = useSelector(state => state.TypeAuth.typeAuth)

    let [ flagCaptcha, setFlagCaptcha] = useState(false)

    function switchTypeAuth(type) {
        if (type === "login") {
            navigate("/Auth/Regist")
            dispatch(changeTypeAuth("login"))
        } else {
            navigate("/Auth/Regist")
            dispatch(changeTypeAuth("reg"))
        }
    }

    return (
        <Container>
            <LogoComp></LogoComp>
            <ContainerAuth>
                <ContainerBtnsNav>
                    <Button type="submit" onClick={() => switchTypeAuth("login")} size="sm">Войти</Button>
                    <Button type="submit" onClick={() => switchTypeAuth("reg")} size="sm">Создать аккаунт</Button>
                </ContainerBtnsNav>
                {typeAuth === "reg" ? <Regist flagCaptcha={flagCaptcha}></Regist> : <Login flagCaptcha={flagCaptcha}></Login>}
            </ContainerAuth>
            <ContainerCaptha>
                <Recaptcha
                    sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                    render="explicit"
                    verifyCallback={() => {
                        setTimeout(() => {
                            setFlagCaptcha(false)
                        }, 60000) 
                        setFlagCaptcha(true)}}
                    onloadCallback={() => { console.log("loaded") }}
                />
            </ContainerCaptha>
        </Container>
    )
}

export default Auth
